INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Feufollet', 'Feufollet is an Americana/Cajun band from Lafayette, Louisiana.', 'Philippe Billeaudeaux, Kelli Jones-Savoy, Chris Segura, Chris Stafford, Mike Stafford, Andrew Toups', (select user_id from users where email_address = 'P.Billeaudeaux@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Steve Riley and the Mamou Playboys', 'Steve Riley and the Mamou Playboys are an American Cajun band from southern Louisiana. The band formed in 1988 and has since recorded 10 albums, nine of which are on Rounder Records.', 'Steve Riley, Sam Broussard, Brazos Huval, Kevin Dugas, Kevin Wimmer', (select user_id from users where email_address = 'S.Riley@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Astral Project', 'Astral Project is a modern jazz quartet from New Orleans, Louisiana. It was founded by saxophonist Tony Dagradi in 1978 and includes drummer Johnny Vidacovich, bassist James Singleton and 7-string guitarist Steve Masakowski.', 'Tony Dagradi, Johnny Vidacovich, James Singleton, Steve Masakowski', (select user_id from users where email_address = 'T.Dagradi@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Better Than Ezra', 'Better Than Ezra is an American alternative rock band based in New Orleans, Louisiana, and signed with The End Records.', 'Kevin Griffin, Tom Drummond, James Arthur Payne Jr., Michael Jerome', (select user_id from users where email_address = 'K.Griffin@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Cha Wa', 'Cha Wa is a Grammy-nominated Mardi Gras Indian funk band based out of New Orleans, Louisiana. The name Cha Wa is a slang phrase used by Mardi Gras Indian tribes, meaning "here we come."', 'Joe Gelini, Honey Banister', (select user_id from users where email_address = 'J.Gelini@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('The Deslondes', 'The Deslondes are an Americana musical group from New Orleans, Louisiana. Their music blends together influences from folk, rock and roll, bluegrass, R&B, American roots music, blues, gospel, country, and zydeco.', 'Dan Cutler, Sam Doores, Riley Downing, Cameron Snyder, John James Tourville', (select user_id from users where email_address = 'D.Cutler@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Egg Yolk Jubilee', 'Egg Yolk Jubilee is a band based in New Orleans and noted for their eclectic musical style, combining jazz, blues, funk, and rock elements. In addition to traditional stage venues, they also perform as a street-walking brass band, and have released several studio albums.', 'Eric Belletto, Paul Grass, Keith Hajjar, Mike Hogan, Mac McCann, Glenn Grass', (select user_id from users where email_address = 'E.Belletto@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Flow Tribe', 'Flow Tribe is an American funk rock band based in New Orleans, Louisiana. The group contains six members who are all natives of New Orleans. The band was founded in 2004, and was featured on Episode 11 of MTVs The Real World: New Orleans, which aired September 8, 2010.', 'K.C. O''Rorke, John-Michael Early, Russell Olschner, Chad Penot, Bryan Santos, Mario Palmisano', (select user_id from users where email_address = 'M.Palmisano@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('Groovesect', 'Groovesect was founded in 2006 by New Orleans musicians who had played together in various incarnations since they were eighteen, blending syncopated funk beats and jazz rhythms with rock and soul. Since "Uganda" joined the band in the spring of 2007, Groovesect has been voted the #1 Funk Band and #2 Contemporary Jazz Band in the 2007 Best of the Big Easy Awards.', 'Danny Abel, Nick Krawitz, Tim Sullivan, Eric Vogel, Colin Davis, Alfred "Uganda" Roberts, Justin Aliperti', (select user_id from users where email_address = 'J.Aliperti@gmail.com'));
INSERT INTO band (band_name, band_description, band_member, manager_id) VALUES ('The Meters', 'The Meters are an American funk band formed in 1965 in New Orleans by Zigaboo Modeliste (drums), George Porter Jr. (bass), Leo Nocentelli (guitar) and Art Neville (keyboards). The band performed and recorded their own music from the late 1960s until 1977 and played an influential role as backing musicians for other artists.', 'George Porter Jr., Leo Nocentelli, Zigaboo Modeliste, Brian Stoltz, Terrence Houston', (select user_id from users where email_address = 'G.Porter@gmail.com'));
